import React, { useState, useEffect, useContext } from 'react';
import { FaBars, FaTimes } from 'react-icons/fa';
import { IconContext } from 'react-icons/lib';
import { Button, LangButton } from '../../globalStyles';
/* import { Context } from '../Wrapper' */
/* import { FormattedMessage } from 'react-intl' */

import tableIcon from "./icon.png"

import {
    Nav,
    NavbarContainer,
    NavLogo,
    MobileIcon,
    NavMenu,
    NavItem,
    NavItemBtn,
    NavLinks,
    NavBtnLink
} from './Navbar2.elements';

function Navbar2() {

/*     const context = useContext(Context) */


    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false);
        } else {
            setButton(true);
        }
    };

    useEffect(() => {
        showButton();
    }, []);

    window.addEventListener('resize', showButton);

    let username = localStorage.getItem('username')


    return (
        <>
            <IconContext.Provider value={{ color: '#fff' }}>
                <Nav>
                    <NavbarContainer>
                        <NavLogo to='/' onClick={closeMobileMenu}>
                            <img style={{ height: 40, width: 40 }} src={tableIcon} alt="Desksharing Logo" />

                            MINT YOUR OWN PIXELART 


                        </NavLogo>
                        <MobileIcon onClick={handleClick}>
                            {click ? <FaTimes /> : <FaBars />}
                        </MobileIcon>
                        <NavMenu onClick={handleClick} click={click}>
                            <NavItem>
                                <NavLinks to='/home' onClick={closeMobileMenu}>

                                    <h1>HOME</h1>

                        {/*             <FormattedMessage
                                        id="navbar.home">./src/components/navbar/Navbar2.js
Module not found: Can't resolve '../Wrapper' in '/home/brate/Desktop/pixelart_creator/react_web3_pixelart_creator/client/src/components/navbar'
                                    </FormattedMessage> */}
                                </NavLinks>
                            </NavItem>
                            <NavItem>
                                {
                                    username !== "admin" ?
                                        <NavLinks to='/services' onClick={closeMobileMenu}>
                                  {/*           <FormattedMessage
                                                id="navbar.services">

                                            </FormattedMessage> */}
                                                <h1>SERVICES</h1>
                                        </NavLinks> : <p> </p>}
                            </NavItem>
                            <NavItem>
                                {
                                    username !== "admin" ?
                                        <NavLinks to='/myBookings' onClick={closeMobileMenu}>
                               {/*              <FormattedMessage
                                eact-int                id="navbar.products">

                                            </FormattedMessage> */}
                                                <h1>PRODUCTS</h1>
                                        </NavLinks> : <p></p>}
                            </NavItem>
                            <NavItem>
                                <NavLinks to='/employeelist' onClick={closeMobileMenu}>
                {/*                     <FormattedMessage
                                        id="navbar.employeefinder">

                                    </FormattedMessage> */}
                                        <h1>ABOUT</h1>
                                </NavLinks>
                            </NavItem>
                            <NavItem>

                  {/*               {
                                    username == "admin" ?

                                        <bg to='/adminbooking' onClick={closeMobileMenu}>
                                            <FormattedMessage
                                                id="navbar.adminbooking">

                                            </FormattedMessage>
                                            <h1>KEIN PLAN</h1>
                                        </NavLinks> : <h1></h1>
                                } */}


                            </NavItem>
                            <NavItemBtn>
                                {button ? (
                                    <NavBtnLink to='/login'>
                                        <Button primary>
                                            {/*                                   <FormattedMessage
                                         id="navbar.login" >

                                        </FormattedMessage> */}
                                            Login

                                        </Button>
                                    </NavBtnLink>
                                ) : (
                                    <NavBtnLink to='/login'>
                                        <Button onClick={closeMobileMenu} fontBig primary>
                                            {/*                                          <FormattedMessage
                                                id="navbar.login">

                                            </FormattedMessage> */}
                                            Login
                                        </Button>
                                    </NavBtnLink>
                                )}
                            </NavItemBtn>



                            <LangButton /* value={context.locale = "de-DE"} onClick={context.selectLang} */>🇩🇪</LangButton>
                            <LangButton /* value={context.locale = "en-US"} onClick={context.selectLang} */>🇺🇸</LangButton>
                            <LangButton /* value={context.locale = "es-MX"} onClick={context.selectLang} */>🇪🇸</LangButton>
                            <LangButton /* value={context.locale = "ru-RU"} onClick={context.selectLang} */>🇷🇺</LangButton>







                        </NavMenu>{/*  */}
                    </NavbarContainer>
                </Nav>
            </IconContext.Provider>
        </>


    );
}

export default Navbar2;
