import React, { Component } from 'react'
import "./Pixelart.css"
class pixelart_paint extends Component {

    componentDidMount(){
        const container = document.querySelector('.container')
        const sizeEl = document.querySelector('.size')
        let size = sizeEl.value
        const color = document.querySelector('.color')
        /* const color = "#00eeff"
         */

        /* const color = "#00eeff" */


        const resetBtn = document.querySelector('.btn')
        const eraserBtn = document.querySelector('.eraserBtn')
        let draw = false

        let eraser = false


        function populate(size) {
          container.style.setProperty('--size', size)
          for (let i = 0; i < size * size; i++) {
            const div = document.createElement('div')
            div.classList.add('pixel')

            div.addEventListener('mouseover', function () {
              if (!draw) return
              div.style.backgroundColor = color.value
            })

            div.addEventListener('mousedown', function () {
              div.style.backgroundColor = color.value
            })


            div.addEventListener('mousedown', function () {
              if (!eraser) return
              div.style.backgroundColor = "#ffffff"
              console.log(eraser)


            })

            container.appendChild(div)
          }
        }

        window.addEventListener("mousedown", function () {
          draw = true
        })
        window.addEventListener("mouseup", function () {
          draw = false
        })

        function reset() {
          container.innerHTML = ''
          populate(size)
        }
        function setColorWhite() {
          if (!eraser) {
            eraser = true;
            console.log(eraser)
          } else {
            eraser = false;
            console.log(eraser)

          }


        }

        resetBtn.addEventListener('click', reset)

        eraserBtn.addEventListener('click', setColorWhite)

        sizeEl.addEventListener('keyup', function () {
          size = sizeEl.value
          reset()
        })

        populate(size)
    }



    render() {
        return (
            <div className="App">
                       <div className="navbar">
          <button className="btn">Reset</button>

          <input type="color" defaultValue="#00eeff" class="color" />



          <button className="eraserBtn">eraser</button>

          <input type="number" defaultValue="20" class="size" />
        </div>

        <div ref={this.ref} className="container" id="container">
        </div>
        <button onClick={this.appendScreenshot}>Load Canvas</button>
        <div id="container2"></div>
        <div id="container3"></div>
            </div>
        );
    }
}

export default pixelart_paint;
