import React, { Component, useState } from 'react'
import "./Pixelart.css"
import html2canvas from "html2canvas"
//import { color } from 'html2canvas/dist/types/css/types/color';
//import { color } from 'html2canvas/dist/types/css/types/color';

class Pixelart extends Component {

  constructor(props) {
    super(props);

    this.state = {
      isHovering: false,
      colorPicker: "#ffffff"

    };
    console.log("colorState:" + this.state.colorPicker)
  }
  componentDidMount() {



    let draw = false
    let eraser = false
    let container = document.querySelector('.container')
    let sizeEl = document.querySelector(".size");
    let size = sizeEl.value
    const color = document.querySelector('.color')
    let resetBtn = document.querySelector('.btn')
    let eraserBtn = document.querySelector('.eraserBtn')


    this.setState({ colorPicker: color.value }, () => {

      function populate(size) {

        container.style.setProperty('--size', size)

        for (let i = 0; i < size * size; i++) {
          let div = document.createElement('div')
          div.classList.add('pixel')




          let colorPicker = getComputedStyle(div)
            .getPropertyValue('--main-bg-color');



          div.addEventListener('mouseover', function () {
            div.style.setProperty("--main-bg-color", color.value);


            if (!draw) return
            div.style.backgroundColor = color.value
            div.style.setProperty("--main-bg-color", color.value);
            div.style.setProperty("--pixel-bg-color", color.value);


          })

          div.addEventListener('mouseover', function () {

            if (getComputedStyle(div)
              .getPropertyValue('--pixel-bg-color') != getComputedStyle(div)
                .getPropertyValue('--main-bg-color')) return
            div.style.backgroundColor = colorPicker;
          })




          div.addEventListener('mousedown', function () {
            div.style.backgroundColor = color.value
            div.style.setProperty("--main-bg-color", color.value);

          })

          /*    div.addEventListener('mouseout',function(){

               if(pixelColor != colorPicker) return
               div.style.backgroundColor = colorPicker;

             }) */

          /*  div.addEventListener('mousedown', function () {
             if (!eraser) return
             div.style.backgroundColor = "#ffffff"
             div.style.setProperty("--main-bg-color", "#ffffff");

           })

           div.addEventListener("mouseover",function(){
             if (!eraser) return
             div.style.backgroundColor = "#ffffff"
             div.style.setProperty("--main-bg-color", getComputedStyle(div)
             .getPropertyValue('--pixel-bg-color'));
           })

           div.addEventListener("mouseout", function(){
             if (!eraser) return
             div.style.backgroundColor = getComputedStyle(div)
             .getPropertyValue('--pixel-bg-color')
           }) */
           div.addEventListener('mousedown', function () {
            if (!eraser) return
            color.value = "#ffffff"

          })

       /*    div.addEventListener("mouseover",function(){
            if (!eraser) return

          }) */
          container.appendChild(div)




        }
      }



      window.addEventListener("mousedown", function () {
        draw = true

      })
      window.addEventListener("mouseup", function () {
        draw = false
      })

      function reset() {
        container.innerHTML = ''
        populate(size)
      }

      function setColorWhite() {
        color.value = "#ffffff"
      }


      resetBtn.addEventListener('click', reset)
      eraserBtn.addEventListener('click', setColorWhite)


      /* resize grid when new value is added */
      sizeEl.addEventListener('keyup', function () {
        size = sizeEl.value
        reset()
      })
      populate(size);

    });
  }

  appendScreenshot = () => {

    const container = document.querySelector("#container");

    html2canvas(container).then((canvas) => {
      const url = canvas.toDataURL(); //png file

      console.log(url) //  base64 

      /* document.querySelector("#container2").appendChild(canvas); */
    });
  }

  render() {
    return <div className="Appi">

      <div className="navbar">
        <button className="btn">Reset</button>
        <button className="eraserBtn">Eraser</button>
        <input type="color" valueDefault="#00eeff" id="color" class="color"></input>
        <input type="number" value="64" class="size"></input>
     </div>

      <div className="container" id="container"></div>

      <button onClick={this.appendScreenshot}>Load Canvas</button>

     {/*   <div className="container2" id="container2"></div> */}


    </div>;

  }
}

export default Pixelart;
